#data = ''
#data = ''
data = ''
data = [int(data[i:i+2], 16) for i in range(0,len(data),2)]
datab = b''
for x in data:
	datab += x.to_bytes(1, byteorder='big')
width, height = 128, 32
img = "P1\n"+str(width)+"\n"+str(height)+"\n"
for y in range(height):
	for x in range(width):
		tilex, tiley = x // 8, y // 8
		bytenum = (tiley * width // 8) * 8 + x % (width)
		b = datab[bytenum]
		bitnum = (y % 8)
		bit = b & (1 << bitnum)
		if bit:
			img += "0 "
		else:
			img += "1 "
open("img.ppm","w").write(img)

